import { Component } from '@angular/core';
import { MatDialogRef } from '@angular/material/dialog';
import { MatDialog } from '@angular/material/dialog';
import {ModalGeneral} from '../modal-general/modal-general'
import {ModalMotivosRechazoComponent} from '../modal-motivos-rechazo/modal-motivos-rechazo.component'
@Component({
  selector: 'app-modal-revision-plan-auditoria',
  templateUrl: './modal-revision-plan-auditoria.component.html',
  styleUrls: ['./modal-revision-plan-auditoria.component.css']
})
export class ModalRevisionPlanAuditoriaComponent {
  constructor(public dialogRef: MatDialogRef<ModalRevisionPlanAuditoriaComponent>, public dialog: MatDialog) {}

  botonSeleccionado: string = 'formato';
  documento: string ='' ;
  
  ngOnInit(): void {
    // Asigna el Base64 a la variable, incluyendo el prefijo del tipo de archivo.
    this.documento = '';
  }
  seleccionarBoton(button: string) {
    this.botonSeleccionado = button;
    this.actualizarFrame(button);
  }
  actualizarFrame(boton: string) {
   
    if (boton === 'formato') {
      this.documento = this.documento;
    } else {
     //segunda opcion
    }
  }
  openModalRechazo(): void {
    this.dialog.open(ModalMotivosRechazoComponent, {
      width: '70vw',
    });
  }
  openModalEnviar(): void {
    this.dialog.open(ModalGeneral, {
      width: '75vw',
      data: {  mensaje: '¿Está seguro de enviar el <br> plan anual de auditoría - PAA?',
        icono: 'warning',
        mostrarBotonCancelar: true,
        textoConfirmacion: 'Aceptar',
        continue:true,
        textoMensajeConfirmacion:'PLAN ENVIADO',
        textoSubMensajeConfirmacion:'Su plan fue enviado a jefe de oficina',
      }
    });
  }
  onClose(): void {
    this.dialogRef.close();
  }
}